        async function updateChartSelisihTrasaksi() {
            try {
                const {
                    data
                } = await axios.get('{{ url('admin/home/ajaxData') }}')
                const chartData = chartPertama.data;
                let result = data;
                let label = [];
                let bulan_ini = [];
                let bulan_kemaren = [];
                for (let i = 0; i < result.length; i++) {
                    label.push(result[i].title);
                    bulan_ini.push(result[i].bulan_ini);
                    bulan_kemaren.push(result[i].bulan_kemaren);
                }
                if (chartData.datasets.length > 0) {
                    chartData.labels = label
                    chartData.datasets[0].data = bulan_ini
                    chartData.datasets[1].data = bulan_kemaren
                }
                chartPertama.update();
            } catch (error) {
                console.log(error);
            }
        }


        $(document).ready(function() {
            'use strict'
            getSaldo('dflash', 'dflash_saldo')
        });

        function getSaldo(biller = '', idTag = '') {
            let dataPost = new FormData();
            dataPost.append("biller", biller)
            axios.post("/home/saldo", dataPost)
                .then((res) => {
                    let data = res.data
                    document.getElementById(idTag).innerHTML = data.saldo
                }).catch((err) => {
                    console.log(err);
                })
        }
    </script>
